import React, {useState} from 'react';
import {Container} from '../style/styled';
import {ModalButton, ModalLayer} from './comp/ModalButtonComp';
import { ModalButtonComp } from './comp/ModalButtonComp';
import {ModalContainer} from './style/ModalStyle';

const CustomModal = (props) =>{

    const [layerDisplay, setLayerDisplay] = useState('none');
    const [containerState, setContainerState] = useState({
        color: 'white',
        opacity: 1
    });

    const onClick = () =>{
        console.log('hi')
        setLayerDisplay('block');
        setContainerState({
            color: 'lightgray',
            opacity: 0.7
        })
    }

    return(

        <>
            <ModalButtonComp 
                click={onClick}
                layerDisplay={layerDisplay}    
                containerState={containerState}
                >
                
            </ModalButtonComp>

            <ModalContainer 
                containerStateColor={containerState.color}
                containerStateOpacity={containerState.opacity}
            >

            </ModalContainer>
        </>
    )
}





export default CustomModal;
