import styled from 'styled-components';
import React, {} from 'react';
import {Container} from '../../style/styled';
import {ModalLayer} from '../style/ModalStyle';

export const ModalComp = (props) =>{


    const ModalContainer = styled(Container)`
    position: relative;
    opacity: ${props.containerState.opacity};
    background-color: ${props.containerState.color};
    height: 180px;
`

    return(
        <>  
            <ModalContainer>
                <h2>Modal</h2> 
                <ModalButton onClick={()=>{
                    props.click();
                    }}>
                        Modal
                </ModalButton>
                <ModalLayer layerDisplay={props.layerDisplay}></ModalLayer>
            </ModalContainer>
        </>
    )
}




export const ModalButton = styled.div`
    dispaly: block;
    width: 80px;
    height: 40px;
    line-height: 40px;
    text-align: center;
    border-radius: 30px;
    background-color: black;
    margin: 0 auto;
    color: white;
    cursor: pointer;
`
