import React, {useState} from 'react';
import {Container} from '../style/styled';
import {ModalButton, ModalLayer} from './comp/ModalComp';
import { ModalComp } from './comp/ModalComp';


const CustomModal = () =>{

    const [layerDisplay, setLayerDisplay] = useState('none');
    const [containerState, setContainerState] = useState({
        color: white,
        opacity: 0
    });

    const onClick = () =>{
        console.log('hi')
        setLayerDisplay('block');
        setContainerState({
            color: black,
            opacity: 50
        })
    }

    return(

        <>
            <ModalComp 
                click={onClick}
                layerDisplay={layerDisplay}    
                containerState={containerState}
                >
                
            </ModalComp>
        </>
    )
}





export default CustomModal;
