import styled from 'styled-components';
import React, {} from 'react';
import {Container} from '../../style/styled';

export const ModalComp = (props) =>{

    const ModalLayer = styled.div`
    width: 100px;
    height: 100px;
    display: ${props.layerDisplay};
    position: absolute;
    background-color: gray;
    top: 30px;
    left: 50%;
    transform: translateX(-50%);
    `

    return(
        <>
            <ModalContainer>
                <h2>Modal</h2> 
                <ModalButton onClick={()=>{
                    props.click();
                    }}>
                        Modal
                </ModalButton>
                <ModalLayer></ModalLayer>
            </ModalContainer>
        </>
    )
}

const ModalContainer = styled(Container)`
    position: relative;
`


export const ModalButton = styled.div`
    dispaly: block;
    width: 80px;
    height: 40px;
    line-height: 40px;
    text-align: center;
    border-radius: 30px;
    background-color: black;
    margin: 0 auto;
    color: white;
    cursor: pointer;
`
